'use strict';

module.exports = (index, state) => {
  // alert(state.characters[index].name + ' is fighting the enemy');

  let player = state.characters[index].startingStats;
  let enemy = state.characters[state.characters.length - 1].startingStats;

  enemy.hp -= player.att + player.mag;
  if (enemy.hp <= 0) {
    let temp = state.characters;
    state.characters = {};
    for (let i = 0; i < state.characters.length - 1; i++) {
      state.characters += temp[i];
      console.log(temp[i]);
    }
    return state;
  }
  player.hp -= enemy.att + enemy.mag;

  return state;
};
