'use strict';

module.exports = (index, state) => {
  // alert(state.characters[index].name + ' is fighting the enemy');

  let player = state.characters[index].startingStats;
  let enemy = state.enemies[0].startingStats;

  turn(player, enemy);
  if (enemy.hp <= 0) {
    state.enemies.splice(0, 1);
    return state;
  }
  turn(enemy, player);
  if (player.hp <= 0) {
    state.characters.splice(index, 1);
  }

  return state;
};

let turn = (char1, char2) => {
  if (char1.speed > char2.speed + 5) {
    char2.hp -= 2 * (char1.att + char1.mag);
  }
  char2.hp -= char1.att + char1.mag;
};
