import React from 'react';
import ReactDOM from 'react-dom';
const fight = require('./fight.js');
const superagent = require('superagent');

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      characters: []
    };
  }

  componentDidMount() {
    superagent
      .get('http://localhost:4000/api/v1/StarterCharacters')
      .then(results => {
        this.setState({ characters: results.body });
      });
  }

  render() {
    let statList = [
      'Health',
      'Attack',
      'Magic',
      'Speed',
      'Luck',
      'Defence',
      'Resistance'
    ];

    return (
      <div className="container">
        {this.state.characters &&
          this.state.characters.map((char, index) => {
            return (
              <div className="character">
                <h3>{char.name}</h3>{' '}
                <div className="row">
                  <div className="col-sm-9">
                    {char.startingStats.map((stat, key) => {
                      return (
                        <>
                          <span key={key}>
                            {statList[key]}: {stat},{' '}
                          </span>
                        </>
                      );
                    })}
                  </div>
                  <div className="col-sm-3">
                    {/* {if(index === this.state.charcters.length - 1)} */}
                    <button
                      key={index}
                      onClick={() => {
                        let newState = fight(index, this.state);
                        this.setState(newState);
                      }}
                      className="btn btn-dark"
                    >
                      Use to fight
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    );
  }
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
